template <typename T>
T Check_Correct(T min, T max)
{
    T x;
    while ((std::cin >> x).fail() || x < min || x>max)
    {
        std::cin.clear();
        std::cin.ignore(INT_MAX, '\n');
        std::cout << "Данные не верные, попробуйте ещё раз:";
    }
    return x;
}


