using StationFilter = bool(*) (Station& s, T par);

template <typename T>
vector <int> SearchPipebyParameter(unordered_map <int, Pipe>& Pipemap, PipeFilter<T> f, T par) {
    vector <int> id;
    for (auto& Pipe : Pipemap) {                                                                                                                                                            
        if (f(Pipe.second, par))
            id.push_back(Pipe.second.GetPipeID());
    }
    return id;
}

template <typename T>
vector <int> SearchSbyParameter(unordered_map <int, Station>& Stationmap, StationFilter<T> f, T par) {
    vector <int> id;
    for (auto& Station : Stationmap) {
        if (f(Station.second, par))
            id.push_back(Station.second.GetStationID());
    }
    return id;
}
